mapper.sql:
SELECT * FROM board 


		BOARD_ID = #{keyword}


		BOARD_SJ LIKE CONCAT('%', #{keyword}, '%')

mapper.select:
SELECT lists.*  
FROM () lists
ORDER BY BOARD_FRST DESC
LIMIT ${perPage}
OFFSET ${offset}

mapper.update:
UPDATE board
SET BOARD_SJ = #{boardSj}, BOARD_CN = #{boardCn}, BOARD_IMG = #{boardImg}, BOARD_LAST = NOW()
WHERE NO = #{UsersVo.NO} AND BOARD_ID = #{boardId}
