mapper.insert:
 INSERT INTO users_file
 (
  saved_name
  , file_name
  , file_path
  , file_ext
  , file_size
  , up_time
  , u_no
 )

 VALUES
(
	#{savedName}
	, #{fileName}
	, #{filePath}
	, #{fileExt}
	, #{fileSize}
	, NOW()
	, #{loginVO.uNo}
)

 INSERT INTO boast_file
 (
  saved_name
  , file_name
  , file_path
  , file_ext
  , file_size
  , up_time
  , boast_id
 )

 VALUES
(
	#{savedName}
	, #{fileName}
	, #{filePath}
	, #{fileExt}
	, #{fileSize}
	, NOW()
	, #{boastId}
)

 INSERT INTO tbl_partner_file
 (
  saved_name
  , file_name
  , file_path
  , file_ext
  , file_size
  , up_time
  , board_id
 )

 VALUES
(
	#{savedName}
	, #{fileName}
	, #{filePath}
	, #{fileExt}
	, #{fileSize}
	, NOW()
	, #{boardId}
)

mapper.update:
 	UPDATE tbl_partner_file
 	SET 
 		saved_name = #{savedName}
 		, file_name = #{fileName}
 		, file_path = #{filePath}
 		, file_ext = #{fileExt}
 		, file_size = #{fileSize}
 		, up_time = NOW()

		WHERE u_no = #{LoginVO.uNo}

mapper.select:
    	SELECT *
		FROM tbl_partner_file tip 
		WHERE tip.partner_no = #{partnerNo}
