mapper.update:
 	UPDATE tbl_partner_file
 	SET 
 		saved_name = #{savedName}
 		, file_name = #{fileName}
 		, file_path = #{filePath}
 		, file_ext = #{fileExt}
 		, file_size = #{fileSize}
